    const res = await fetch('https://pokeapi.co/api/v2/pokemon?limit=151')
    const data = await res.json();
    console.log(data);

    setLista(data.results);
  }

  useEffect(() => {  getData();}, []);
 
 const handleChange = (e)  => {
   this.setState({id:e.value, name:e.label})
  }

  const componentDidMount = ()  => {
