import { createContext, useContext, useState } from "react";

export const PokemonesContext = createContext();

export default function PokemonesProvider({ children }) {
  const [pokemones, setPokemones] = useState([]);
 
  const addPokemones = (id,imagen,desc) => {
    /* cargar en favoritos solo una vez aunque la seleccione muchas veces*/
    let ind = pokemones.findIndex((ele) => ele.id == id);
    if (ind==-1)
    {
      setPokemones([...pokemones, {id,imagen,desc}]);
    }

  };

 
 
  return (
     <PokemonesContext.Provider value={{ pokemones, addPokemones}}>
      {children}
      </PokemonesContext.Provider>
  );
}

// mini hook
export const useFavoritosContext = () => useContext(FavoritosContext);
