import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
const { id } = useParams();
const navigate = useNavigate();

export default function  ListadoPokemon () {

  const [listadoPokemon, setListadoPokemon] = useState([]);
 

  const getData = async () => {
    try {
    //const res = await fetch('https://pokeapi.co/api/v2/pokemon?limit=151')
    const res = await fetch('pokemones.json')
    const data = await res.json();
    setListadoPokemon(data.results);
  } catch (error) {
    navigate("/pokemones");
  }
  }

  useEffect(() => {  getData();}, []);
  

  const seleccionado = (item) =>{
     item.url;
     

}


  return (
    <div>
        <select id="select" className="listado">
        {listadoPokemon.map((item) => {
           
 
           return (
            <option id={item.url}>{item.name}</option>
                  );
 
         })}
        </select>
        <button className="boton" onClick={(e) => { seleccionado(item)  }} >Ver Detalle</button>
    </div>

);}
